sp_widget.client_script:
function($scope, $rootScope, spUtil) {
	var c = this;

	c.saving = {};

	$rootScope.$on('selectNote', function(event, noteID) {
		c.server.get({
			action: 'getNote',
			noteID: noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});

	c.updateNote = function(updateType) {
		c.saving[updateType] = false;
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(reply) {
			c.saving[updateType] = true;
			if (reply.data.note) {
				$rootScope.$emit('updatedNote', reply.data.note);
			}
		});

		setTimeout(function() {
			delete c.saving[updateType];
		}, 2000);
	};
}

sp_widget.css:
.fade {
  &.ng-hide {
  	opacity: 0;
  }
  &.ng-hide-remove,
  &.ng-hide-add {
    display: block !important;
  }

  &.ng-hide-remove {
    transition: all linear 1000ms;
  }

  &.ng-hide-add {
    transition: all linear 500ms;
  }

}

sp_widget.script:
(function() {
  /* populate the 'data' object */

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, 'title,note,sys_id');
			} else if (input.action == 'updateNote') {
				note.title = input.noteTitle;
				note.note = input.noteBody;
				note.update();

				data.note = {};
				$sp.getRecordValues(data.note, note ,'title,note,sys_id');

				if (data.note.note.length > 20) {
					data.note.note = data.note.note.slice(0, 20);
				}
			}
		}
	}

})();
